import React, { useState } from 'react';
import {Button} from 'antd'
import { Table, Space } from 'antd';
import {Container} from './style';
import Relatorio from '../Relatorio/index';
import Parecer from '../Modal/index';

export default function Tabela({btns, children}){
    const [selectedRowKeys, setSelectedRowKeys] = useState([]);
    const [leitura, setLeitura] = useState(false);
    const [visible, setVisible] = useState(false);
    const [relatorios, setRelatorios] = useState([]);

    const columns = [
        {
          title: 'aluno',
          dataIndex: 'aluno',
          key: 'aluno',
          render: aluno => <a>{aluno}</a>,
        },
        {
          title: 'email',
          dataIndex: 'email',
          key: 'email',
        },
        {
          title: 'ra',
          dataIndex: 'ra',
          key: 'ra',
        },
        {
          title: 'curso',
          dataIndex: 'curso',
          key: 'curso',
        },
        {
          title: 'orientador',
          dataIndex: 'orientador',
          key: 'orientador',
        },
        {
          title: 'action',
          key: 'action',
          render: (text, record) => (
            <Space size="middle">
              <Button onClick={()=>setLeitura(true)}>Ler</Button>
              <Button onClick={()=>setVisible(true)}>Parecer</Button>
            </Space>
          ),
        },
      ];

    let data = [];

    for(let i=1; i<40; i++){
      data.push({
        key: `${i}`,
        aluno: 'Carlos',
        email: 'carlitos@unesp.br',
        ra: '554112341',
        curso: 'Mestrado',
        orientador: 'Almir'
      })
    }

    setRelatorios(data);

    return(
        <Container>
            {leitura ? <Relatorio setLeitura={setLeitura}></Relatorio>:
            <Table columns={columns} dataSource={relatorios} pagination={{ pageSize: 8 }}  />}
            <Parecer visible={visible} setVisible={setVisible}></Parecer>
        </Container>
    );
}
