import styled from 'styled-components';

export const Container = styled.div `
  width: 100vw;
  height: 100vh;
  display: grid;
  grid-template-columms: 10vw 90vw;
  background-color: black;

  > header {
    display: flex;
    justify-content: space-between;
    width: 95%;
    margin: auto;

    > button {
      padding: 10px;
      border-radius: 4px;
      font-weight: bold;
      :hover {
      }
    }
  }

  > section {
    width: 95%;
    margin: 20px auto;

    > form {
      display: flex;
      align-items: center;
      justify-content: space-between;
      margin: 10px auto;

      .MuiFormControl-root {
        width: 80%;
      }
    }
  }

  .no-items {
    width: 100%;
    text-align: center;
    margin: 10px 0;
  }
`;
