import styled from 'styled-components';

export const Container = styled.div `
  width: 100vw;
  height: 100vh;
  display: grid;
  grid-template-columns: 15vw 85vw;
  background-color: black;

  > nav {
    grid-column-start: 1;
    grid-column-end: 2;
    height: 100vh;
    background-color: blue;
  }

  > section {
    width: 95%;
    margin: 20px auto;

    > form {
      display: flex;
      align-items: center;
      justify-content: space-between;
      margin: 10px auto;

      .MuiFormControl-root {
        width: 80%;
      }
    }
  }

  .no-items {
    width: 100%;
    text-align: center;
    margin: 10px 0;
  }
`;
